import React, {useEffect, useState} from 'react';
import './news.css';
import NewsListedSmall from './NewsListedSmall';
import SecondaryButton from '../buttons/SecondaryButton'
import image from '../../images/kids.jpg'
import data from './news-data';

export default function NewsListSmall(props){

        const [count, setCount] = useState(6);
        const [elements, setElements] = useState([])

        function handleLoadMore(){
            setCount(count + 4)
        }

        useEffect(()=> {
            var arr = data;
            let elementsTemp = [];

            if(arr.length > 0){
                for(var i = 0; i < count; i++)
            {
            elementsTemp.push(
                <NewsListedSmall
                    title={arr[i].title}
                    src={arr[i].src}
                    />
                )
            }
        setElements(elementsTemp)
            }
        }, [props.data, count])

    return (
        <div className="list-small-container">
            <div className="listed-news">
                {elements}
            </div>
            <SecondaryButton
                text={props.buttonText}
                onAction={handleLoadMore}
            />
        </div>
    )
}
