import './pages.css';
import React, { useEffect, useState} from 'react';
import HeadBackground from '../components/header/HeadBackground';
import HeadTitle from '../components/header/HeadTitle';
import NextLast from '../components/match/NextLast';
import UpcomingMatches from '../components/match/UpcomingMatches';
import Navbar from '../components/navbar/Navbar';
import image from '../images/main-head.jpg';
import MainPageNews from '../components/news/MainPageNews';
import OtherPagesPanel from '../components/panel/OtherPagesPanel';
import Footer from '../components/footer/Footer';





export default function Home() {
  const [contentTop, setContentTop] = useState(800);
  function updateContentTop(){
    if(window.innerWidth > 1400){
      setContentTop(800)
      document.getElementsByClassName('content-container')[0].style.top = contentTop;
    }
    else if(window.innerWidth > 800){
      setContentTop(600)
      document.getElementsByClassName('content-container')[0].style.top = contentTop;
    }
    else if(window.innerWidth > 500){
      setContentTop(200)
      document.getElementsByClassName('content-container')[0].style.top = contentTop;
    }
  }

  useEffect(() => {
      window.addEventListener("resize", updateContentTop);

      return () => window.removeEventListener("resize", updateContentTop);
  })

  return (
    <div className="wrapper">
      <Navbar />
      <div>
        <div className="container">
          <HeadBackground 
            src={image}
            />
          <HeadTitle 
            text="SPR BÓR Oborniki Śląskie"
            />
          <UpcomingMatches />
        <div className="content-container">
            <NextLast />
            <MainPageNews />
            <OtherPagesPanel />
            <Footer />
          </div>
        </div>
      </div>
    </div>
  );
}
